import React, { useState } from 'react';
import Base from '../core/Base';
import { isAuthenticated } from '../auth/helper';
import { Link } from 'react-router-dom';


const AddCategory = () => {


    const [name, setname] = useState();
    const [eroor, seteroor] = useState(false);
    const [success, setsuccess] = useState(false);

    const { user, token } = isAuthenticated();

    const myform = () => {
        return (
            <form className="form-group mb-3">
                <label className="mt-2">Enter your Category</label>
                <input type="text"
                    className="form-control mt-2"
                    autoFocus
                    placeholder="Ex. Summer Tshirt"
                />
                <button type="button" className="btn btn-success mt-3">Add Category</button>
            </form>
        )
    }

    const goBack = () => (
        <Link className="btn btn-primary mb-5" to="/admin/dashbord">Admin Home</Link>
    )

    return (
        <Base>
            <div className="container text-muted text-center">
                <h1 className="heading-2 mt-3">Create Category here</h1>
            </div>
            <div className="container mt-3">
                <div className="row rounded bg-white">
                    <div className="col-md-8 offset-md-2">
                        {myform()}
                        {goBack()}
                    </div>
                </div>
            </div>
        </Base>
    );
}

export default AddCategory;
