import React, { useState } from 'react';
import Base from '../core/Base';
import { Link, Redirect } from 'react-router-dom';
import { signin, authenticate } from '../auth/helper/index';

const Signin = () => {

    const [values, setValues] = useState({
        email: "",
        password: "",
        error: "",
        loading: false,
        didRedirect: false
    });

    const { email, password, error, loading, didRedirect } = values;

    const handleChange = name => event => {
        setValuses({ ...values, error: false, [name]: event.target.value })
    }

    const onSubmit = event => {
        event.preventDefault()
        setValues({ ...values, error: false, loading: true })
        signin({ email, password })
            .then(data => {
                if (data.error) {
                    setValues({ ...values, error: data.error, loading: false })
                } else {
                    authenticate(data, () => {
                        setValues({ ...values, didRedirect: true })
                    })
                }
            })
            .catch(error => console.log(error));
    }

    const successMsg = () => {
        return (
            <div className="alert alert-success"
                style={{ display: success ? "" : "none" }}
            >
                New account has been created successfuly.please<Link to="/signin">login here</Link>
            </div>
        );
    }

    const errorMsg = () => {
        return (
            <div className="alert alert-danger"
                style={{ display: error ? "" : "none" }}
            >
                {error}
            </div>
        );
    }


    const SigninForm = () => (
        <div className="container">
            <div className="container text-center mt-3">
                <h3 className="text-muted">Sign In</h3>
            </div>
            <div className='form-group'>
                <label for='exampleInputEmail1'>Email address</label>
                <input
                    type='email'
                    className='form-control'
                    id='exampleInputEmail1'
                    aria-describedby='emailHelp'
                    placeholder='Enter email'
                />
                <small id='emailHelp' className='form-text text-muted'>
                    We'll never share your email with anyone else.
          </small>
            </div>
            <div className='form-group'>
                <label for='exampleInputPassword1'>Password</label>
                <input
                    type='password'
                    className='form-control'
                    id='exampleInputPassword1'
                    placeholder='Password'
                />
            </div>
            <div className="text-center mb-3">
                <button type='submit' className='btn btn-success alien-center'>
                    Submit
                </button>
            </div>
        </div>
    )
    return (
        <Base>
            {SigninForm()}
        </Base>
    )
}
export default Signin;
