import React, { useState } from 'react';
import Base from '../core/Base';
import { Link } from 'react-router-dom';
import { getCategories } from './helper/adminapicall';

const AddProduct = () => {

    const [value, setValue] = useState({
        name: "",
        description: "",
        stock: "",
        price: "",
        photo: "",
        category: "",
        categories: [],
        loading: false,
        error: "",
        createdProduct: "",
        getRedirect: false,
        formData: ""
    });

    const { name, description, stock, price, categories, category, loading, error, createdProduct, getRedirect, formData } = value

    const preload = () => {
        getCategories().then(data => {
            if (data.error) {
                setValue({ ...value, error: data.error })
            } else {
                setValue({ ...value, categories: data, formData: new FormData() })
            }
        }).catch(err => console.log(err))
    }

    const handleChange = name => event => {
        setValue({ ...value, [name]: event.target.value })
    }

    const onSubmit = () => {
        //
    }

    const createProductForm = () => (
        <form >
            <span>Post photo</span>
            <div className="form-group">
                <label className="btn btn-block btn-success">
                    <input
                        onChange={handleChange("photo")}
                        type="file"
                        name="photo"
                        accept="image"
                        placeholder="choose a file"
                    />
                </label>
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("name")}
                    name="photo"
                    className="form-control"
                    placeholder="Name"
                    value={name}
                />
            </div>
            <div className="form-group">
                <textarea
                    onChange={handleChange("description")}
                    name="photo"
                    className="form-control"
                    placeholder="Description"
                    value={description}
                />
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("price")}
                    type="number"
                    className="form-control"
                    placeholder="Price"
                    value={price}
                />
            </div>
            <div className="form-group">
                <select
                    onChange={handleChange("category")}
                    className="form-control"
                    placeholder="Category"
                >
                    <option>Select</option>
                    <option value="a">a</option>
                    <option value="b">b</option>
                </select>
            </div>
            <div className="form-group">
                <input
                    onChange={handleChange("quantity")}
                    type="number"
                    className="form-control"
                    placeholder="Quantity"
                    value={stock}
                />
            </div>

            <button type="submit" onClick={onSubmit} className="btn btn-outline-success mb-3">
                Create Product
          </button>
        </form>
    );

    return (
        <Base>
            <div className="container mt-3">
                <div className="row bg-white rounded mb-3">
                    <div className="col-md-8 offset-md-2">
                        {createProductForm()}
                        <Link className="btn btn-outline-primary mb-3" to="/admin/dashbord">Admin Home</Link>
                    </div>
                </div>
            </div>
        </Base>
    );
}

export default AddProduct;
